const timeStamp = new Date(msg.payload);
const eventType = msg.topic;
let domain = "stndDefined";
// handle domain expecption for 'pnfRegistration'
if (eventType === 'pnfRegistration') {
    domain = eventType;
} 
const nfNamingCode = env.get('APP_NETWORK_FUNCTION_ID');
const namespaces = {
    pnfRegistration: undefined,
    o1NotifyPnfRegistration: 'o1-notify-pnf-registration',
    oRanScDuHelloWorldPmStreaming: 'o-ran-sc-du-hello-world-pm-streaming-oas3',
    notifyHeartbeat: '3GPP-Heartbeat',
    notifyFileReady: 'file-ready'
};
const topic = namespaces[eventType] || msg.topic;

msg.topic = "commonEventHeader"
msg.payload = {
    domain: domain,
    eventType: eventType,
    eventId:[nfNamingCode, eventType, msg._msgid].join('_'),
    eventName: [domain, eventType].join('_'),
    sequence: Number(timeStamp),
    priority: "Low",
    reportingEntityId: "c2b7d6e9-ee35-459a-ab8e-717a6fc1fde6",
    reportingEntityName: global.get('os').hostname(),
    sourceId: "378e9904-6d39-40ea-9994-0596fe2235a3",
    sourceName: env.get('APP_SOURCE'),
    startEpochMicrosec: timeStamp.getTime() * 1000,
    lastEpochMicrosec: timeStamp.getTime() * 1000,
    nfNamingCode: nfNamingCode,
    nfVendorName: env.get('APP_VENDOR'),
    timeZoneOffset: "+00:00",
    stndDefinedNamespace: topic,
    version: "4.1",
    vesEventListenerVersion: "7.2.1"
}
return msg;

const timeStamp = new Date(msg.payload);
const startTime = new Date(Math.floor(Number(timeStamp) / 900000) * 900000);
const endTime = new Date(Number(startTime) + 900000);
const paddy = (num, padlen, padchar) => {
    const pad_char = typeof padchar !== 'undefined' ? padchar : '0';
    const pad = new Array(1 + padlen).join(pad_char);
    return (pad + num).slice(-pad.length);
}
const fileStartTime = [
    paddy(startTime.getFullYear(), 4),
    paddy(startTime.getMonth() + 1, 2),
    paddy(startTime.getDate(), 2),
    '.',
    paddy(startTime.getHours(), 2),
    paddy(startTime.getMinutes(), 2),
    '+0000'
].join('');
const fileEndTime = [
    paddy(endTime.getHours(), 2),
    paddy(endTime.getMinutes(), 2),
    '+0000'
].join('');
const fileLocation = ['/pm-data-files/A', fileStartTime, '-', fileEndTime, '_', env.get('APP_NETWORK_FUNCTION_ID'), '.xml'].join('');
msg.payload = {
    schemaReference: 'https://forge.3gpp.org/rep/sa5/MnS/raw/Rel-18/OpenAPI/TS28532_FileDataReportingMnS.yaml#components/schemas/NotifyFileReady',
    stndDefinedFieldsVersion: '1.0',
    data: {
        href: global.get('os').hostname(),
        notificationId: Number(startTime),
        notificationType: msg.topic,
        eventTime: timeStamp.toISOString(),
        systemDN: '/',
        fileInfoList: [
            {
                fileLocation: fileLocation,
                fileSize: 1234,
                fileReadyTime: startTime.toISOString(),
                fileExpirationTime: endTime.toISOString(),
                fileCompression: 'no',
                fileFormat: 'xml',
                fileDataType: 'Performance'
            }
        ],
        additionalText: 'Have fun!'
    }
};
msg.topic = 'stndDefinedFields';
return msg;

const timeStamp = new Date(msg.payload);
msg.payload = {
