const string = msg.payload;
const array = JSON.parse(string);
if (Array.isArray(array)) {
    msg.payload = array.map( (item) => {
        if (typeof item === 'string' || item instanceof String) {
            return JSON.parse(item);
        }
    });
} else {
    msg.payload = array;
}
return msg;

const base = 'https://messages.smo.o-ran-sc.org';
const path = 'events';
const urlMapping = {
    pnfRegistration: "unauthenticated.VES_PNFREG_OUTPUT",
    o1NotifyPnfRegistration: "unauthenticated.VES_O1_NOTIFY_PNF_REGISTRATION_OUTPUT",
    oRanScDuHelloWorldPmStreaming: "unauthenticated.VES_O_RAN_SC_HELLO_WORLD_PM_STREAMING_OUTPUT",
    notifyHeartbeat: "unauthenticated.SEC_3GPP_HEARTBEAT_OUTPUT",
    notifyFileReady: "unauthenticated.VES_FILE_READY_OUTPUT",
    oRanOpenfrontManagementPlane: "unauthenticated.VES_O_RAN_SC_OPENFRONTHAUL_OUTPUT"
};
const urlTopic = urlMapping[msg.topic] || msg.topic.replace(':', '-');
msg.url = [base, path, urlTopic, '1','1'].join('/');
return msg;

const base = 'https://odlux.oam.smo.o-ran-sc.org';
const path = 'rests/operations/network-topology:network-topology/topology=topology-netconf/node=';
const nodeId = msg.payload;
const mount = 'yang-ext:mount';
const action = 'o-ran-supervision:supervision-watchdog-reset';

msg.url = [base,path + nodeId, mount, action].join('/');
msg.payload = { 
  "o-ran-supervision:input": 
    {
      "supervision-notification-interval": 60, 
      "guard-timer-overhead": 10
    }
}
return msg;

// read topic data from global context
const topicData = global.get('topicData');
// init result as array
let result = [];
if (Array.isArray(topicData)) {
    result = topicData.filter(event => {
        return event.event.commonEventHeader.stndDefinedNamespace === 'o-ran-supervision:supervision-notification';
    }).map(event => {
        return event.event.commonEventHeader.sourceName;;
    });
}
msg.payload = result;
global.set('topicData', []);
return msg;
